// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { restaurantApi } from '../api/restaurantApi';
// In your Dashboard.js or relevant component
import LocationSearch from './LocationSearch';

// Inside your component's render/return:

const Dashboard = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();
    const [restaurants, setRestaurants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        fetchRestaurants();
    }, []);

    const fetchRestaurants = async () => {
        try {
            const data = await restaurantApi.getAllRestaurants();
            setRestaurants(data);
            setError('');
        } catch (err) {
            setError('Failed to fetch restaurants');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = async (e) => {
        e.preventDefault();
        if (!searchTerm.trim()) {
            fetchRestaurants();
            return;
        }

        setLoading(true);
        try {
            const data = await restaurantApi.searchRestaurants(searchTerm);
            setRestaurants(data);
            setError('');
        } catch (err) {
            setError('Failed to search restaurants');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    if (loading) {
        return <div className="flex justify-center items-center h-screen">Loading...</div>;
    }

    return (


        <div className="min-h-screen bg-gray-100">
            <nav className="bg-white shadow-lg">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between h-16">
                        <div className="flex items-center">
                            <h1 className="text-xl font-bold">Restaurant Finder</h1>
                        </div>
                        <div className="flex items-center">
                            <button
                                onClick={handleLogout}
                                className="ml-4 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </nav>

            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
    <LocationSearch />

    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-6">
        {restaurants.map((restaurant) => (
            <div key={restaurant.id} className="bg-white overflow-hidden shadow rounded-lg">
                <div className="px-4 py-5 sm:p-6">
                    <h3 className="text-lg font-medium text-gray-900">{restaurant.name}</h3>
                    <p className="mt-1 text-sm text-gray-600">{restaurant.description}</p>
                    <div className="mt-4">
                        <p className="text-sm text-gray-500">Location: {restaurant.location}</p>
                        <p className="text-sm text-gray-500">Cuisine: {restaurant.cuisine}</p>
                        <button
                            onClick={() => navigate(`/restaurant/${restaurant.id}`)}
                            className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors">
                            View Reviews
                        </button>
                    </div>
                </div>
            </div>
        ))}
    </div>
</main>
        </div>

    );
};

export default Dashboard;
