import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { restaurantApi } from '../api/restaurantApi';
import { Search } from 'lucide-react';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

const Dashboard = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();
    const [restaurants, setRestaurants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Search states
    const [searchTerm, setSearchTerm] = useState('');
    const [category, setCategory] = useState('');
    const [priceRange, setPriceRange] = useState('');
    const [rating, setRating] = useState('');

    useEffect(() => {
        fetchRestaurants();
    }, []);

    const fetchRestaurants = async () => {
        try {
            const data = await restaurantApi.getAllRestaurants();
            setRestaurants(data);
            setError('');
        } catch (err) {
            setError('Failed to fetch restaurants');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            // Build query parameters
            const params = new URLSearchParams();
            if (searchTerm.trim()) params.append('q', searchTerm.trim());
            if (category) params.append('category', category);
            if (priceRange) params.append('price', priceRange);
            if (rating) params.append('rating', rating);

            // If no filters are applied, fetch all restaurants
            if (!params.toString()) {
                fetchRestaurants();
                return;
            }

            const data = await restaurantApi.searchRestaurants(params.toString());
            setRestaurants(data);
            setError('');
        } catch (err) {
            setError('Failed to search restaurants');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleClear = () => {
        setSearchTerm('');
        setCategory('');
        setPriceRange('');
        setRating('');
        fetchRestaurants();
    };

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    if (loading) {
        return <div className="flex justify-center items-center h-screen">Loading...</div>;
    }

    return (
        <div className="min-h-screen bg-gray-100">
            <nav className="bg-white shadow-lg">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between h-16">
                        <div className="flex items-center">
                            <h1 className="text-xl font-bold">Restaurant Finder</h1>
                        </div>
                        <div className="flex items-center space-x-4">
                            <button
                                onClick={() => navigate('/location-search')}
                                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700"
                            >
                                Search by Location
                            </button>
                            <button
                                onClick={handleLogout}
                                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </nav>

            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                <div className="mb-6 bg-white p-6 rounded-lg shadow">
                    <form onSubmit={handleSearch} className="space-y-4">
                        <div className="flex gap-4">
                            <div className="relative flex-1">
                                <input
                                    type="text"
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                    placeholder="Search restaurants by name..."
                                    className="w-full p-2 pr-10 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                                />
                                <Search className="absolute right-3 top-2.5 text-gray-400" size={20} />
                            </div>

                            <Select value={category} onValueChange={setCategory}>
                                <SelectTrigger className="w-[180px]">
                                    <SelectValue placeholder="Category" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="italian">Italian</SelectItem>
                                    <SelectItem value="chinese">Chinese</SelectItem>
                                    <SelectItem value="indian">Indian</SelectItem>
                                    <SelectItem value="mexican">Mexican</SelectItem>
                                    <SelectItem value="japanese">Japanese</SelectItem>
                                </SelectContent>
                            </Select>

                            <Select value={priceRange} onValueChange={setPriceRange}>
                                <SelectTrigger className="w-[180px]">
                                    <SelectValue placeholder="Price Range" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="$">$ (Under $10)</SelectItem>
                                    <SelectItem value="$$">$$ ($11-$30)</SelectItem>
                                    <SelectItem value="$$$">$$$ ($31-$60)</SelectItem>
                                    <SelectItem value="$$$$">$$$$ (Above $60)</SelectItem>
                                </SelectContent>
                            </Select>

                            <Select value={rating} onValueChange={setRating}>
                                <SelectTrigger className="w-[180px]">
                                    <SelectValue placeholder="Rating" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="4.5">4.5+ ⭐</SelectItem>
                                    <SelectItem value="4">4+ ⭐</SelectItem>
                                    <SelectItem value="3.5">3.5+ ⭐</SelectItem>
                                    <SelectItem value="3">3+ ⭐</SelectItem>
                                </SelectContent>
                            </Select>
                        </div>

                        <div className="flex gap-4">
                            <button
                                type="submit"
                                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                            >
                                Search
                            </button>
                            {(searchTerm || category || priceRange || rating) && (
                                <button
                                    type="button"
                                    onClick={handleClear}
                                    className="px-4 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
                                >
                                    Clear All
                                </button>
                            )}
                        </div>
                    </form>
                </div>

                {error && (
                    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                        {error}
                    </div>
                )}

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {restaurants.map((restaurant) => (
                        <div key={restaurant.id} className="bg-white overflow-hidden shadow rounded-lg">
                            <div className="px-4 py-5 sm:p-6">
                                <h3 className="text-lg font-medium text-gray-900">{restaurant.name}</h3>
                                <p className="mt-1 text-sm text-gray-600">{restaurant.description}</p>
                                <div className="mt-4">
                                    <p className="text-sm text-gray-500">Location: {restaurant.location}</p>
                                    <p className="text-sm text-gray-500">Cuisine: {restaurant.cuisine}</p>
                                    <button
                                        onClick={() => navigate(`/restaurant/${restaurant.id}`)}
                                        className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors">
                                        View Reviews
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                {restaurants.length === 0 && !loading && !error && (
                    <div className="text-center text-gray-500 mt-4">
                        No restaurants found.
                    </div>
                )}
            </main>
        </div>
    );
};

export default Dashboard;
