import React, { useState, useRef } from 'react';
import { searchApi } from '../api/searchApi';

const LocationSearch = () => {
    const [query, setQuery] = useState('');
    const [suggestions, setSuggestions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [debugOutput, setDebugOutput] = useState(''); // For debug information
    const debounceTimeout = useRef(null);

    const addDebugMessage = (message) => {
        setDebugOutput(prev => prev + message + '\n');
    };

    const handleQueryChange = async (e) => {
        const value = e.target.value;
        setQuery(value);
        addDebugMessage(`Input changed: ${value}`);

        // Clear existing timeout
        if (debounceTimeout.current) {
            clearTimeout(debounceTimeout.current);
        }

        if (value.trim().length >= 3) {
            debounceTimeout.current = setTimeout(async () => {
                setLoading(true);
                addDebugMessage(`\nSearching for: ${value}`);

                const token = localStorage.getItem('token');
                if (!token) {
                    addDebugMessage('Error: No JWT token provided');
                    setError('No authentication token found');
                    setLoading(false);
                    return;
                }

                try {
                    const response = await fetch(
                        `http://localhost:8081/api/search/locations?query=${encodeURIComponent(value.trim())}`,
                        {
                            headers: {
                                'Authorization': `Bearer ${token}`,
                                'Accept': 'application/json'
                            }
                        }
                    );

                    addDebugMessage(`Response status: ${response.status}`);
                    const responseText = await response.text();
                    addDebugMessage(`Raw response: ${responseText}`);

                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }

                    let suggestions;
                    try {
                        suggestions = JSON.parse(responseText);
                        setSuggestions(suggestions);
                        addDebugMessage(`Parsed ${suggestions.length} suggestions`);
                    } catch (e) {
                        throw new Error(`Failed to parse JSON: ${e.message}\nRaw response: ${responseText}`);
                    }

                } catch (err) {
                    setError(err.message);
                    addDebugMessage(`Error: ${err.message}`);
                    console.error('Error:', err);
                } finally {
                    setLoading(false);
                }
            }, 300);
        } else {
            setSuggestions([]);
        }
    };

    return (
        <div className="max-w-4xl mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Location Search</h1>

            <div className="relative mb-4">
                <input
                    type="text"
                    value={query}
                    onChange={handleQueryChange}
                    className="w-full p-2 border border-gray-300 rounded-md"
                    placeholder="Enter location (e.g., Boston)"
                    autoComplete="off"
                />

                {loading && (
                    <div className="absolute right-3 top-3">
                        <div className="animate-spin h-4 w-4 border-2 border-blue-500 rounded-full border-t-transparent"></div>
                    </div>
                )}

                {suggestions.length > 0 && (
                    <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-y-auto">
                        {suggestions.map((suggestion, index) => (
                            <div
                                key={index}
                                className="p-3 hover:bg-gray-100 cursor-pointer"
                            >
                                <div>{suggestion.displayName}</div>
                                <div className="text-sm text-gray-600">
                                    ({suggestion.latitude}, {suggestion.longitude})
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {error && (
                <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded-md">
                    {error}
                </div>
            )}

            <div className="mt-4 p-3 bg-gray-100 border border-gray-300 rounded-md whitespace-pre-wrap font-mono text-sm">
                {debugOutput}
            </div>
        </div>
    );
};

export default LocationSearch;
