// src/api/authApi.js
const API_BASE_URL = 'http://localhost:8081';

export const authApi = {
  login: async (credentials) => {
    try {
      console.log('Sending login request with:', credentials); // Debug log
      const response = await fetch(`${API_BASE_URL}/auth/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Login error:', errorData); // Debug log
        throw new Error(errorData.message || 'Login failed');
      }

      const data = await response.json();
      console.log('Login response:', data); // Debug log
      return data;
    } catch (error) {
      console.error('Login error:', error); // Debug log
      throw error;
    }
  },
  // ... rest of the code
  signup: async (userData) => {
    const response = await fetch(`${API_BASE_URL}/auth/signup`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    });
    if (!response.ok) {
      throw new Error('Signup failed');
    }
    return response.json();
  },
};

// src/components/LoginForm.js - Update the form submission
const LoginForm = () => {
  // ... existing state declarations

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const credentials = {
        email: email,
        password: password
      };

      console.log('Submitting credentials:', credentials); // Debug log

      const response = await authApi.login(credentials);
      console.log('Login successful:', response); // Debug log

      login(response);
      navigate('/dashboard');
    } catch (err) {
      console.error('Login error in component:', err); // Debug log
      setError('Failed to login. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  // ... rest of the component
};
